 datastoreManager.saveAccessToken(result.data?.token ?: "")
 }

 is Resource.Error -> {
 _state.value =
 LoginState(error = result.message ?: "An unexpected error occurred")
 }

 is Resource.Loading -> {
 _state.value = LoginState(isLoading = true)
 }
 }
 }.launchIn(viewModelScope)
 }

