 context,
 getAvailableFrontCameraId(context),
 object : Camera2Capturer.Listener {
 override fun onFirstFrameAvailable() {}
 override fun onCameraSwitched(newCameraId: String) {}
 override fun onError(error: Camera2Capturer.Exception) {
